import React from "react";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Card from "react-bootstrap/Card";
import Select from "react-select";

function Home() {
  useEffect(() => {
    fetchItems();
  }, []);

  const [items, setItems] = useState([]);

  const [breed, setBreed] = useState({});
  const [catResults, setCatResults] = useState([]);
  item;

  useEffect(() => {
    fetchCategories();
  }, [breeds]);

  async function handleSetBreeds(selected) {
    setBreed(selected);
    const string = selected.id;
    const url = `https://api.thecatapi.com/v1/images/search?breed_ids=${string}`;

    //local variable can have the same name as above
    const response = await axios.get(url);
    const newItems = response.data;
    setItems(newItems);
  }

  // const fetchItems = async () => {
  //   var x = document.getElementById("mySelect").value;
  //   const string = "x";
  //   const data = await fetch(
  //     `https://api.themoviedb.org/3/movie/${string}?api_key=153693fafc14ce487b5a217264c74cb1&language=en-US&page=1`
  //   );
  //   const items = await data.json();
  //   console.log(items.results);
  //   setItems(items.results);
  // };
  const url = `https://image.tmdb.org/t/p/w500/`;

  return (
    <div>
      <select id="mySelect" onChange={handleChange}>
        {/* name={name}  onChange={handleChange} */}
        <option value="populat">Popular</option>
        <option value="top-rated">Top Rated</option>
        <option value="upcoming">Upcoming</option>
        <option value="now_playing">Now Playing</option>
      </select>
      {items.slice(0, 12).map(item => (
        <Card
          key={item.id}
          style={{
            width: "18rem",
            backgroundColor: "#379683",
            marginBottom: "5px"
          }}
        >
          <Link to={"/home/" + item.id}>
            <Card.Img variant="top" src={url + item.poster_path} />
            <Card.Body>
              <Card.Title>{item.title}</Card.Title>
              <Card.Text>
                <p>{item.release_date}</p>
                <p>{item.vote_average}</p>
                <p>{item.overview}</p>
                <a href="#">More Info</a>
              </Card.Text>
            </Card.Body>
          </Link>
        </Card>
      ))}
    </div>
  );
}

export default Home;
