import React, { useState, useEffect } from "react";

function IndividualMovie({ match }) {
  useEffect(() => {
    fetchItem();
    console.log(match);
  }, []);

  const [item, setItemS] = useState({
    imgae: {}
  });

  const fetchItems = async () => {
    const fetchItem = await fetch(
      `https://api.themoviedb.org/3/movie/{match.params.id}?api_key=153693fafc14ce487b5a217264c74cb1&language=en-US&page=1`
    );
    const items = await fetchItem.json();
    setItems(items);
    console.log(items);
  };

  return (
    <div>
      <h1>Individual Page</h1>
      <p>{item.id}</p>
    </div>
  );
}

export default IndividualMovie;
