import React from "react";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Axios from "axios";
import "./styles.css";
import $ from "jquery";

function SearchResult() {
  const [movies, setMovies] = useState("");

  useEffect(() => {
    handleChange();
  }, [movies]);

  async function handleChange(selected) {
   // const choice = document.getElementById("mySelect").value;
    // const url = `https://api.themoviedb.org/3/movie/${choice}?api_key=153693fafc14ce487b5a217264c74cb1&language=en-US`;
    // const response = await Axios.get(url);
    // const items = response.data;
    // setItems(items.results);
   // movies = {};
    performSearch();
  }

  function performSearch(){
    console.log("Perform search");
    const urlString = `https://api.themoviedb.org/3/search/movie?query=avengers&api_key=153693fafc14ce487b5a217264c74cb1&language=en-US`;
    $.ajax({
        url: urlString,
        success: (searchResults) => {
          console.log("Fetched successfully");
          console.log(searchResults);
          //const results contain all the movies that we are searching for
          const results = searchResults.results;
        //  console.log(results[0]);

        var movieRows = [];
          results.forEach((movie) => {
              console.log(movie.title);
            //  const movieRow = <MovieRow movie={movie} />
          movieRows.push(<p> movie title : {movie.title}</p>);
          console.log(movieRows[0]); 
        })

           // setMovies[{rows : movieRows}]
        },
        error: (xhr, status, err) =>{
          console.error("Failed to fetch data")
        }

    })
  }
  return (
   <div className="container">
      <input
      type="search"
      placeholder="Search Movie by Title"
      aria-label="Search"
      name="search"
      onChange={handleChange}
    />
    <p>This is a row</p>
   </div>
  );
}

export default SearchResult;
